mapper.select:
select
  id, sku_id, title, sub_title, ali_image, price, ali_images_1, ali_images_2, ali_images_3, ali_images_4, type_ob, old_or, score, create_time, update_time, user_id
from freefish.tb_good


        and id = #{id}


        and sku_id = #{skuId}


        and title = #{title}


        and sub_title = #{subTitle}


        and ali_image = #{aliImage}


        and price = #{price}


        and ali_images_1 = #{aliImages1}


        and ali_images_2 = #{aliImages2}


        and ali_images_3 = #{aliImages3}


        and ali_images_4 = #{aliImages4}


        and type_ob = #{typeOb}


        and old_or = #{oldOr}


        and score = #{score}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and user_id = #{userId}
