mapper.select:
select
  id, sku_id, title, sub_title, ali_image, price, ali_images_1, ali_images_2, ali_images_3, ali_images_4, type_ob, old_or, score, create_time, update_time, user_id, score_num,good_state,postage
from freefish.tb_good


        and price >= #{lowerPrice}
        and price <= #{highPrice}


        and price >= #{lowerPrice}


        and price <= #{highPrice}


        and type_ob = #{typeOb}


        and old_or = #{oldOr}



    order by price


    order by ${sort} desc
